import React, { Component } from 'react';
import { Pane, Heading, Text, Badge, Pill } from 'evergreen-ui'

class Article extends Component {

  render() {
    const { title, summary, badges } = this.props;
    return (
      <Pane display="flex" padding={16} margin={16} background="tint2" borderRadius={3} flexDirection="column">
        <Heading size={600}>{title} <Pill display="inline-flex" margin={8}>0</Pill></Heading>
        <Text color="muted" marginTop="default">{summary}</Text>
        <Pane paddingTop={16}>
        {badges.map((b)=><Badge color={b.color} key={b.label} marginRight={8}>{b.label}</Badge>)}
        </Pane>
      </Pane>
    );
  }

}

export default Article;
